package translator;

import java.io.StringReader;
import java_cup.runtime.DefaultSymbolFactory;
import translator.ast.Program;

public class DebugProbe {
    private static void runCase(String title, String src) {
        System.out.println("==== " + title + " ====");
        try {
            Lexer lexer = new Lexer(new StringReader(src));
            Parser parser = new Parser(lexer, new DefaultSymbolFactory());
            Program p = (Program) parser.parse().value;
            System.out.println("Parsed tests: " + p.tests.size());
            try {
                Validator.validate(p);
                System.out.println("Validator: OK (no errors)");
            } catch (ParseException ex) {
                System.out.println("Validator error: " + ex.getMessage());
            }
        } catch (ParseException ex) {
            System.out.println("Parse error: " + ex.getMessage());
        } catch (Exception e) {
            e.printStackTrace();
        }
        System.out.println();
    }

    public static void main(String[] args) throws Exception {
        String src = String.join("\n",
            "config { base_url = \"http://localhost:8080\"; };",
            "test T {",
            "  GET \"/api/users/$id\";",
            "  expect status = 200;",
            "  expect body contains \"x\";",
            "}");
        Lexer lexer = new Lexer(new StringReader(src));
        Parser parser = new Parser(lexer, new DefaultSymbolFactory());
        Program p = (Program) parser.parse().value;
        System.out.println("Parsed tests: " + p.tests.size());
        try {
            Validator.validate(p);
            System.out.println("Validator: OK (no errors)");
        } catch (ParseException ex) {
            System.out.println("Validator error: " + ex.getMessage());
        }

        String case1 = String.join("\n",
            "let 2a = \"x\";",
            "test T { GET \"/x\"; expect status = 200; expect body contains \"x\"; }");
        runCase("invalid let ident", case1);

        String case2 = String.join("\n",
            "test T { GET \"/x\" expect status = 200; expect body contains \"x\"; }");
        runCase("missing semicolon after GET", case2);
    }
}
